programlisting.code:
Create Spacecraft aSat;
Create Propagator aProp;

Create XYPlot aPlot1 aPlot2 aPlot3;

GMAT aPlot1.XVariable = aSat.ElapsedSecs;
GMAT aPlot1.YVariables = {aSat.EarthMJ2000Eq.X};

GMAT aPlot2.XVariable = aSat.ElapsedSecs;
GMAT aPlot2.YVariables = {aSat.EarthMJ2000Eq.Y};

GMAT aPlot3.XVariable = aSat.ElapsedSecs;
GMAT aPlot3.YVariables = {aSat.EarthMJ2000Eq.VX, aSat.EarthMJ2000Eq.VY, aSat.EarthMJ2000Eq.VZ};

BeginMissionSequence;

Propagate aProp(aSat) {aSat.ElapsedDays = 2};
ClearPlot aPlot1 aPlot2 aPlot3;

Create Spacecraft aSat;
Create Propagator aProp;

Create XYPlot aPlot1;

GMAT aPlot1.XVariable = aSat.ElapsedDays;
GMAT aPlot1.YVariables = {aSat.EarthMJ2000Eq.X, aSat.EarthMJ2000Eq.Y};

BeginMissionSequence;

Propagate aProp(aSat) {aSat.ElapsedDays = 3};
ClearPlot aPlot1;
Propagate aProp(aSat) {aSat.ElapsedDays = 1};
