programlisting.code:
%  Create default spacecraft and FuelTank and attach the fuel tank to the spacecraft.
Create Spacecraft DefaultSC;
Create FuelTank FuelTank1;
DefaultSC.Tanks = {FuelTank1};

%  Set FuelTank1 parameters to default values
GMAT FuelTank1.AllowNegativeFuelMass = false;
GMAT FuelTank1.FuelMass = 756;
GMAT FuelTank1.Pressure = 1500;
GMAT FuelTank1.Temperature = 20;
GMAT FuelTank1.RefTemperature = 20;
GMAT FuelTank1.Volume = 0.75;
GMAT FuelTank1.FuelDensity = 1260;
GMAT FuelTank1.PressureModel = PressureRegulated;

%  Create impulsive burn associated with the created fuel tank
Create ImpulsiveBurn IB;
GMAT IB.CoordinateSystem = Local;
GMAT IB.Origin = Earth;
GMAT IB.Axes = VNB;
GMAT IB.Element1 = 0.1;
GMAT IB.Element2 = 0;
GMAT IB.Element3 = 0;
GMAT IB.DecrementMass = true;
GMAT IB.Tank = {FuelTank1};
GMAT IB.Isp = 300;
GMAT IB.GravitationalAccel = 9.810000000000001;

BeginMissionSequence;
%  Apply impulsive maneuver to DefaultSC
Maneuver IB(DefaultSC);
