programlisting.code:
Create Spacecraft aSat;
Create ReportFile aReport;

Create Propagator aProp;

BeginMissionSequence;

Report aReport aSat.UTCGregorian aSat.Earth.SMA aSat.Earth.ECC aSat.Earth.TA aSat.EarthMJ2000Eq.RAAN

Propagate aProp(aSat) {aSat.ElapsedDays = 2};

Report aReport aSat.UTCGregorian aSat.Earth.SMA aSat.Earth.ECC aSat.Earth.TA aSat.EarthMJ2000Eq.RAAN

Create ReportFile aReport;

Create Variable aVar aVar2
GMAT aVar = 100;
GMAT aVar2 = 2000;

Create Array aArray[2,2];
GMAT aArray(1, 1) = 2;
GMAT aArray(1, 2) = 3;
GMAT aArray(2, 1) = 4;
GMAT aArray(2, 2) = 5;

Create String aString
GMAT aString = 'GMAT is awesome';

BeginMissionSequence;

Report aReport aVar aVar2 aArray(1,1) aArray(1,2) aArray(2,1) aArray(2,2) aString;
