programlisting.code:
Create Spacecraft aSat
Create Propagator aProp

Create OrbitView anOrbitView
anOrbitView.Add = {aSat, Earth}

BeginMissionSequence

Toggle anOrbitView Off
Propagate aProp(aSat) {aSat.ElapsedDays = 2}
Toggle anOrbitView On
Propagate aProp(aSat) {aSat.ElapsedDays = 4}

Create Spacecraft aSat aSat2 aSat3 aSat2.INC = 45.0 aSat3.INC = 90.0 aSat3.SMA = 9000
Create Propagator aProp Create Propagator bProp
Create OrbitView anOrbitView anOrbitView2
anOrbitView.Add = {aSat, aSat2, Earth} anOrbitView2.Add = {aSat3, Earth}
BeginMissionSequence
Propagate aProp(aSat, aSat2) bProp(aSat3) {aSat.ElapsedSecs = 12000.0}

Create Spacecraft aSat
Create Propagator aProp
Create OrbitView anOrbitView anOrbitView.Add = {aSat, Earth}
anOrbitView.CoordinateSystem = EarthMJ2000Eq anOrbitView.ViewPointReference = Earth anOrbitView.ViewPointVector = [ 0 0 40000 ] anOrbitView.ViewDirection = Earth anOrbitView.ViewScaleFactor = 1 anOrbitView.ViewUpCoordinateSystem = EarthMJ2000Eq anOrbitView.ViewUpAxis = Z
BeginMissionSequence
Propagate aProp(aSat) {aSat.ElapsedDays = 1}

Create Spacecraft aSat
Create Propagator aProp
Create OrbitView anOrbitView anOrbitView.Add = {aSat, Earth, Luna}
anOrbitView.CoordinateSystem = EarthMJ2000Eq anOrbitView.ViewPointReference = Earth anOrbitView.ViewPointVector = [ 0 0 500000 ] anOrbitView.ViewDirection = Earth anOrbitView.ViewScaleFactor = 1 anOrbitView.ViewUpCoordinateSystem = EarthMJ2000Eq anOrbitView.ViewUpAxis = Z
BeginMissionSequence
Propagate aProp(aSat) {aSat.ElapsedDays = 5}

Create Spacecraft aSat
Create Propagator aProp
Create OrbitView anOrbitView anOrbitView.Add = {aSat, Earth, Luna}
anOrbitView.CoordinateSystem = EarthMJ2000Eq anOrbitView.ViewPointReference = Earth anOrbitView.ViewPointVector = Luna anOrbitView.ViewDirection = aSat anOrbitView.ViewScaleFactor = 1 anOrbitView.ViewUpCoordinateSystem = EarthMJ2000Eq anOrbitView.ViewUpAxis = Z
BeginMissionSequence
Propagate aProp(aSat) {aSat.ElapsedDays = 5}

Create Spacecraft aSat
Create CoordinateSystem LunaMJ2000Eq LunaMJ2000Eq.Origin = Luna LunaMJ2000Eq.Axes = MJ2000Eq
aSat.CoordinateSystem = LunaMJ2000Eq aSat.SMA = 7300 aSat.ECC = 0.4 aSat.INC = 90 aSat.RAAN = 270 aSat.AOP = 315 aSat.TA = 180
Create ForceModel aFM aFM.CentralBody = Luna aFM.PointMasses = {Luna}
Create Propagator aProp aProp.FM = aFM
Create OrbitView anOrbitView anOrbitView.Add = {aSat, Luna, Earth} anOrbitView.CoordinateSystem = LunaMJ2000Eq anOrbitView.ViewPointReference = Luna anOrbitView.ViewPointVector = Earth anOrbitView.ViewDirection = Luna anOrbitView.ViewScaleFactor = 1; anOrbitView.ViewUpCoordinateSystem = LunaMJ2000Eq; anOrbitView.ViewUpAxis = Z;
BeginMissionSequence Propagate aProp(aSat) {aSat.ElapsedDays = 5}

Create Spacecraft aSat
GMAT aSat.DateFormat = UTCGregorian; GMAT aSat.Epoch = '01 Apr 2013 00:00:00.000' GMAT aSat.CoordinateSystem = EarthMJ2000Eq GMAT aSat.DisplayStateType = Cartesian GMAT aSat.X = 1429457.8833484 GMAT aSat.Y = 147717.32846679 GMAT aSat.Z = -86529.655549364 GMAT aSat.VX = -0.037489820883615 GMAT aSat.VY = 0.32032521614858 GMAT aSat.VZ = 0.15762889268226
Create Barycenter EarthMoonBarycenter GMAT EarthMoonBarycenter.BodyNames = {Earth, Luna}
Create LibrationPoint ESL1 GMAT ESL1.Primary = Sun GMAT ESL1.Secondary = EarthMoonBarycenter GMAT ESL1.Point = L1
Create ForceModel aFM aFM.CentralBody = Earth aFM.PointMasses = {Luna, Sun}
Create Propagator aProp aProp.FM = aFM
Create CoordinateSystem SunEarthMoonL1 GMAT SunEarthMoonL1.Origin = ESL1 GMAT SunEarthMoonL1.Axes = ObjectReferenced GMAT SunEarthMoonL1.XAxis = R GMAT SunEarthMoonL1.ZAxis = N GMAT SunEarthMoonL1.Primary = Sun GMAT SunEarthMoonL1.Secondary = EarthMoonBarycenter
Create OrbitView anOrbitView anOrbitView.Add = {aSat, Earth, Sun} anOrbitView.CoordinateSystem = SunEarthMoonL1 anOrbitView.ViewPointReference = ESL1 anOrbitView.ViewPointVector = [ 0 0 30000 ] anOrbitView.ViewDirection = ESL1 anOrbitView.ViewScaleFactor = 25 anOrbitView.ViewUpCoordinateSystem = SunEarthMoonL1 anOrbitView.ViewUpAxis = Z
BeginMissionSequence Propagate aProp(aSat) {aSat.ElapsedDays = 15}

Create Spacecraft aSat aSat2
Create Propagator aProp

Create OrbitView anOrbitView
anOrbitView.Add = {}

BeginMissionSequence
Propagate aProp(aSat, aSat2){aSat.ElapsedSecs = 12000.0}

Create Spacecraft aSat
Create Propagator aProp

Create OrbitView anOrbitView
anOrbitView.Add = {aSat, Earth}

BeginMissionSequence

Propagate aProp(aSat) {aSat.ElapsedDays = 1}
