programlisting.code:
Create Spacecraft MarsExpress MarsExpress.NAIFId = -41 MarsExpress.NAIFIdReferenceFrame = -41001 MarsExpress.Attitude = 'SpiceAttitude' MarsExpress.OrbitSpiceKernelName = ... {'../data/vehicle/ephem/spk/MarsExpress_Short.BSP'} MarsExpress.AttitudeSpiceKernelName = ... {'../data/vehicle/ephem/spk/MarsExpress_ATNM_PTR00012_100531_002.BC'} MarsExpress.SCClockSpiceKernelName = ... {'../data/vehicle/ephem/spk/MarsExpress_MEX_100921_STEP.TSC'} MarsExpress.FrameSpiceKernelName = ... {'../data/vehicle/ephem/spk/MarsExpress_MEX_V10.TF'}
Create Propagator spkProp spkProp.Type = SPK spkProp.StepSize = 60 spkProp.CentralBody = Mars spkProp.EpochFormat = 'UTCGregorian' spkProp.StartEpoch = '01 Jun 2010 16:59:09.815'
Create CoordinateSystem MarsMJ2000Eq MarsMJ2000Eq.Origin = Mars MarsMJ2000Eq.Axes = MJ2000Eq
Create OrbitView Enhanced3DView1 Enhanced3DView1.Add = {MarsExpress, Mars} Enhanced3DView1.CoordinateSystem = MarsMJ2000Eq Enhanced3DView1.ViewPointReference = Mars Enhanced3DView1.ViewPointVector = [ 10000 10000 10000 ] Enhanced3DView1.ViewDirection = Mars
BeginMissionSequence
Propagate spkProp(MarsExpress) {MarsExpress.ElapsedDays = 0.2}

Create Spacecraft aSat ;
GMAT aSat.Attitude = CCSDS-AEM;
GMAT aSat.AttitudeFileName = ...
         '../data/vehicle/ephem/ccsds/CCSDS_BasicEulerFile.aem'

Create Propagator aProp;

Create OrbitView a3DView
a3DView.Add = {aSat,Earth}

BeginMissionSequence;

Propagate aProp(aSat) {aSat.ElapsedSecs = 3600};

Create Spacecraft aSat; 
GMAT aSat.Attitude = PrecessingSpinner;
GMAT aSat.NutationReferenceVectorX = 0;
GMAT aSat.NutationReferenceVectorY = 0;
GMAT aSat.NutationReferenceVectorZ = 1;
GMAT aSat.BodySpinAxisX = 0;
GMAT aSat.BodySpinAxisY = 0;
GMAT aSat.BodySpinAxisZ = 1;
GMAT aSat.InitialPrecessionAngle = 0;
GMAT aSat.PrecessionRate = 1;
GMAT aSat.NutationAngle = 30;
GMAT aSat.InitialSpinAngle = 0;
GMAT aSat.SpinRate = 2;

Create OrbitView OrbitView1;
OrbitView1.Add = {aSat, Earth}
OrbitView1.ViewPointReference = Earth
OrbitView1.ViewPointVector = [ 30000 0 0 ]

Create Propagator aProp
aProp.MaxStep = 10

BeginMissionSequence

Propagate aProp(aSat) {aSat.ElapsedSecs = 12000.0}
