programlisting.code:
Create Spacecraft aSat;
GMAT aSat.ModelFile = '../data/vehicle/models/aura.3ds';
GMAT aSat.ModelOffsetX = 1.5;
GMAT aSat.ModelOffsetY = -2;
GMAT aSat.ModelOffsetZ = 3;
GMAT aSat.ModelRotationX = 180;
GMAT aSat.ModelRotationY = 180;
GMAT aSat.ModelRotationZ = 90;
GMAT aSat.ModelScale = 15;

Create Propagator aProp;

Create OrbitView aOrbitView;
GMAT aOrbitView.Add = {aSat, Earth};

BeginMissionSequence;
Propagate aProp(aSat) {aSat.ElapsedSecs = 9000};
